import React, { Suspense } from "react";
import classes from "./ActivityParameterInfo.module.scss";
import { Canvas } from "react-three-fiber";
import Skeleton from "../Skeleton/Skeleton";

const Activityparameterinfo = (props) => {
  let switched = props.switched;
  let display = {};
  //console.log("Display!!", props.display);
  if (switched) {
    display = props.display;
  }
  console.log("Display!!", display);
  // console.log("current: ", props.currentActivity);
  // console.log("display: ", display);
  // console.log("switched", switched);
  return (
    <div className={classes.activityParameterInfo}>
      <div className={classes.message}>
        <h2 className={classes.title}>Define activity parameters</h2>
        <p className={classes.activityParameterMessage}>
          Add activity parameters here
        </p>
      </div>
      <div className={classes.heightRange}>
        <h2 className={classes.heightRangeTitle}>Select height range</h2>
        <p className={classes.heightRangeMessage}>Enter height range here</p>
        <div className={classes.heightInput}>
          <div className={classes.heightMessage}>
            <p className={classes.heightMessageFrom}>From</p>
            <p className={classes.heightMessageTo}>To</p>
          </div>
          <div className={classes.heightQuery}>
            <input
              className={classes.heightFromFt}
              name="fromFt"
              onChange={props.onChange}
              value={display.fromFt}
              type="number"
            />
            <h3 className={classes.ft1}>ft</h3>
            <input
              className={classes.heightFromIn}
              name="fromIn"
              onChange={props.onChange}
              value={display.fromIn}
              type="number"
            />
            <h3 className={classes.in1}>in</h3>
            <input
              className={classes.heightToFt}
              name="toFt"
              onChange={props.onChange}
              value={display.toFt}
              type="number"
            />
            <h3 className={classes.ft2}>ft</h3>
            <input
              className={classes.heightToIn}
              name="toIn"
              onChange={props.onChange}
              value={display.toIn}
              type="number"
            />
            <h3 className={classes.in2}>in</h3>
          </div>
        </div>
      </div>
      <div className={classes.bmiRange}>
        <h2 className={classes.bmiRangeTitle}>Select BMI range</h2>
        <p className={classes.bmiRangeMessage}>Enter BMI range here</p>
        <div className={classes.bmiInput}>
          <div className={classes.bmiMessage}>
            <p className={classes.bmiMessageFrom}>From</p>
            <p className={classes.bmiMessageTo}>To</p>
          </div>
          <div className={classes.bmiQuery}>
            <input
              className={classes.bmiFrom}
              name="fromBMI"
              onChange={props.onChange}
              value={display.fromBMI}
              type="number"
            />
            <input
              className={classes.bmiTo}
              name="toBMI"
              onChange={props.onChange}
              value={display.toBMI}
              type="number"
            />
          </div>
        </div>
      </div>
      <div className={classes.sensorPlacement}>
        <h2 className={classes.sensorPlacementTitle}>Place sensors</h2>
        <p className={classes.sensorPlacementMessage}>
          Please place sensors on the skeleton below.
        </p>
        <div className={classes.sensorPlacementMain}>
          <Canvas
            concurrent
            invalidateFrameloop
            shadowMap
            camera={{ position: [0, 5, 10], fov: 40 }}
            resize={{ scroll: false }}
          >
            <ambientLight intensity={0.2} />
            <spotLight
              castShadow
              angle={Math.PI / 8}
              intensity={0.2}
              position={[20, 30, 20]}
              shadow-mapSize-width={2048}
              shadow-mapSize-height={2048}
            />
            <Suspense fallback={null}>
              <Skeleton />
            </Suspense>
          </Canvas>
        </div>
      </div>
    </div>
  );
};
export default Activityparameterinfo;
