import React, { useState, useEffect } from "react";
import { BrowserRouter, Route } from "react-router-dom";
import classes from "./App.module.scss";

import HomePage from "./pages/HomePage/HomePage";
import ActivityParameterPage from "./pages/ActivityParameterPage/ActivityParameterPage";
import VideoParameterPage from "./pages/VideoParametersPage/VideoParameterPage";

const App = () => {
  /* Global States */
  const [activities, setActivities] = useState([]);
  const [hasActivities, setHasActivites] = useState(false);
  const [currentActivity, setCurrentActivity] = useState("");
  const [currentPath, setCurrentPath] = useState("/");

  /* Homapage States */
  const [tags, setTags] = useState([]);
  const suggested = [
    { id: "Singing", name: "Singing" },
    { id: "Dancing", name: "Dancing" },
    { id: "Kicking", name: "Kicking" },
    { id: "Typing", name: "Typing" },
    { id: "Running", name: "Running" },
    { id: "Lifting", name: "Lifting" },
  ];

  /* Activity Parameter States */
  const [activityParameters, setActivityParameters] = useState([]);
  const [switched, setSwitch] = useState(false);
  const [currentDisplay, setDisplay] = useState({});
  const [hasActivityParameters, setHasActivityParameters] = useState(false);
  const [allActivitiesCompleted, setAllActivitiesCompleted] = useState(false);
  const [checkPopUp, setCheckPopUp] = useState(false);

  /***----------------------------Homepage Handlers-----------------------------***/
  const addFromSelected = (event) => {
    const suggested = event.target.name.toString();
    console.log({
      id: suggested,
      text: suggested,
    });
    if (!activities.some((a) => a.id === suggested && a.name === suggested)) {
      const tempActivities = [...activities];
      const tempTags = [...tags];
      tempTags.push({
        id: suggested,
        text: suggested,
      });
      tempActivities.push({
        id: suggested,
        name: suggested,
      });
      setHasActivites(true);
      setActivities(tempActivities);
      setTags(tempTags);
      console.log("activities:", activities);
    }
  };

  const addActivityHandler = (tag) => {
    const tempTags = [...tags];
    tempTags.push(tag);
    console.log(tags);
    const tempActivities = [...activities];
    tempActivities.push({
      id: tag.id,
      name: tag.text,
    });

    setTags(tempTags);
    setActivities(tempActivities);
    setHasActivites(true);
  };

  const deleteActivityHandler = (index) => {
    const tempTags = [...tags];
    const tempActivities = [...activities];

    tempTags.splice(index, 1);
    tempActivities.splice(index, 1);

    console.log(tempTags);

    if (tempTags.length === 0) {
      setHasActivites(false);
    }

    setTags(tempTags);
    setActivities(tempActivities);
  };

  const routeToActivityParameters = (event) => {
    const temp = [];
    console.log("activity length: ", activities.length);

    for (var i = 0; i < activities.length; i++) {
      let current = activities[i];
      let currentId = current.id;
      let flag = false;
      let tempActivity = {};
      for (var j = 0; j < activityParameters.length; j++) {
        if (activityParameters[j].id === currentId) {
          flag = true;
          tempActivity = activityParameters[j];
          break;
        }
      }
      if (flag) {
        temp.push(tempActivity);
      } else {
        temp.push({
          id: currentId,
          fromFt: "",
          fromIn: "",
          toFt: "",
          toIn: "",
          fromBMI: "",
          toBMI: "",
        });
      }
    }

    console.log("temp: ", temp);
    setActivityParameters(temp);
    console.log("actvitivy params", activityParameters);
    setCurrentActivity(activities[0].id);
    setDisplay(temp[0]);

    setCurrentPath("/activity-parameters");
  };
  /***-------------------------End of Homepage Handlers-----------------------------***/

  /***-----------------------Activity Parameter Page Handlers-----------------------***/
  const updateParametersHandler = (event) => {
    setSwitch(false);
    console.log(switched);
    const name = event.target.name;
    let value = event.target.value;
    const temp = activityParameters;

    for (var i = 0; i < activityParameters.length; i++) {
      if (activityParameters[i].id === currentActivity) {
        if (name === "fromFt") {
          temp[i].fromFt = value;
        } else if (name === "fromIn") {
          temp[i].fromIn = value;
        } else if (name === "toFt") {
          temp[i].toFt = value;
        } else if (name === "toIn") {
          temp[i].toIn = value;
        } else if (name === "fromBMI") {
          temp[i].fromBMI = value;
        } else if (name === "toBMI") {
          temp[i].toBMI = value;
        }

        const activity = temp[i];
        if (activity.id === currentActivity) {
          console.log("activity check: ", activity);
          if (
            activity.fromFt !== "" &&
            activity.fromIn !== "" &&
            activity.fromBMI !== "" &&
            activity.toBMI !== "" &&
            activity.toIn !== "" &&
            activity.toFt !== ""
          ) {
            setHasActivityParameters(true);
            break;
          }
          setAllActivitiesCompleted(true);
        } else {
          setHasActivityParameters(false);
        }
        break;
      }
    }
    for (var j = 0; j < temp.length; j++) {
      const temp_activity = temp[j];
      if (
        temp_activity.fromFt === "" ||
        temp_activity.fromIn === "" ||
        temp_activity.fromBMI === "" ||
        temp_activity.toBMI === "" ||
        temp_activity.toIn === "" ||
        temp_activity.toFt === ""
      ) {
        setAllActivitiesCompleted(false);
      }

      setActivityParameters(temp);
      //console.log(activityParameters);
      console.log("all completed?", allActivitiesCompleted);
    }
  };

  const switchActivity = (event) => {
    const name = event.target.name;
    const prev = currentActivity;
    if (name !== prev) {
      setSwitch(true);
      setHasActivityParameters(false);
    }
    setCurrentActivity(name);
    for (var i = 0; i < activityParameters.length; i++) {
      if (activityParameters[i].id === name) {
        //console.log("Display fromFt: ", activityParameters[i].fromFt);
        setDisplay(activityParameters[i]);
        console.log("Display fromFt: ", activityParameters[i].fromFt);
        break;
      }
    }
    console.log("switched", switched);

    for (var i = 0; i < activityParameters.length; i++) {
      if (activityParameters[i].id === name) {
        const activity = activityParameters[i];
        if (activity.id === name) {
          console.log("activity check: ", activity);
          if (
            activity.fromFt !== "" &&
            activity.fromIn !== "" &&
            activity.fromBMI !== "" &&
            activity.toBMI !== "" &&
            activity.toIn !== "" &&
            activity.toFt !== ""
          ) {
            setHasActivityParameters(true);
          }
          break;
        }
      }
    }
  };

  const routeBackToHomePage = (event) => {
    setCurrentPath("/");
    setCurrentActivity("");
  };

  const routeToVideoParameterPage = (event) => {
    if (allActivitiesCompleted) {
      setCheckPopUp(false);
    } else {
      setCheckPopUp(true);
    }
  };

  /***--------------------End of Activity Parameter Page Handlers----------------***/

  /***-----------------------Video Parameter Page Handlers-----------------------***/
  /* Victor's state handlers here:

  */
  const routeBackToActivityParameterPage = (event) => {
    setCurrentPath("/activity-parameters");
    setCurrentActivity(activityParameters[0].id);
    setDisplay(activityParameters[0]);
  };

  /***---------------------End of Video Parameter Page Handlers------------------***/

  return (
    <BrowserRouter className={classes.app}>
      <Route path={"/"} exact>
        <HomePage
          hasActivities={hasActivities}
          suggested={suggested}
          tags={tags}
          addActivityHandler={addActivityHandler}
          deleteActivityHandler={deleteActivityHandler}
          addFromSelected={() => addFromSelected}
          routeToActivityParameters={routeToActivityParameters}
        />
      </Route>
      <Route path={"/activity-parameters"} exact>
        <ActivityParameterPage
          activities={activities}
          activityParameters={activityParameters}
          currentActivity={currentActivity}
          switched={switched}
          currentDisplay={currentDisplay}
          hasActivityParameters={hasActivityParameters}
          allActivitiesCompleted={allActivitiesCompleted}
          checkPopUp={checkPopUp}
          updateParametersHandler={updateParametersHandler}
          switchActivity={switchActivity}
          routeBackToHomePage={routeBackToHomePage}
          routeToVideoParameterPage={routeToVideoParameterPage}
        />
      </Route>
      <Route path={"/video-parameters"} exact>
        <VideoParameterPage
          activities={activities}
          routeBackToActivityParameterPage={routeBackToActivityParameterPage}
        />
      </Route>
    </BrowserRouter>
  );
};

export default App;
