import * as THREE from "three";
import React, { useRef, useState, useEffect } from "react";
import { useThree, useFrame } from "react-three-fiber";
import { useDrag } from "react-use-gesture";
import { useGLTF, useAnimations } from "@react-three/drei";

export default function Skeleton(props) {
  const group = useRef();
  const sensorsRef = useRef();
  const { nodes, materials, animations } = useGLTF("../../../Xbot.glb");
  const { actions } = useAnimations(animations, group);
  const [head, setHead] = useState(false);
  const [leftLowerArm, setLeftLowerArm] = useState(false);
  const { size, viewport } = useThree();
  const aspect = size.width / viewport.width;
  const [position, setPosition] = useState([0, 0, 0]);

  const sensorColor = "#575b61";
  const sensorColorClicked = "#582eff";

  materials.Beta_Joints_MAT.color = {
    b: 1,
    g: 1,
    r: 1,
  };

  materials["asdf1:Beta_HighLimbsGeoSG2"].color = {
    b: 0.5,
    g: 0.5,
    r: 0.5,
  };

  const bind = useDrag(
    ({ offset: [x, y] }) => {
      const [, , z] = position;
      setPosition([x / aspect, -y / aspect, z]);
    },
    { pointerEvents: true }
  );

  return (
    <mesh ref={group} {...props} dispose={null} position={[0, -0.9, 0]}>
      <mesh scale={[0.01, 0.01, 0.01]}>
        <primitive object={nodes.mixamorigHips} />
        <mesh position={[0, 180, 0]} onClick={(e) => setHead(!head)}>
          <sphereBufferGeometry args={[2]} attach="geometry" />
          <meshBasicMaterial
            opacity={0.3}
            color={head ? sensorColorClicked : sensorColor}
            attach="material"
          />
        </mesh>
        <mesh
          position={[55, 147, -5]}
          onClick={(e) => setLeftLowerArm(!leftLowerArm)}
        >
          <sphereBufferGeometry args={[2]} attach="geometry" />
          <meshBasicMaterial
            opacity={0.3}
            color={leftLowerArm ? sensorColorClicked : sensorColor}
            attach="material"
          />
        </mesh>
        <skinnedMesh
          material={materials.Beta_Joints_MAT}
          geometry={nodes.Beta_Joints.geometry}
          skeleton={nodes.Beta_Joints.skeleton}
        />
        <skinnedMesh
          material={materials["asdf1:Beta_HighLimbsGeoSG2"]}
          geometry={nodes.Beta_Surface.geometry}
          skeleton={nodes.Beta_Surface.skeleton}
        />
      </mesh>
    </mesh>
  );
}

useGLTF.preload("../../../Xbot.glb");
