/*
Auto-generated by: https://github.com/pmndrs/gltfjsx
*/

import * as THREE from "three";
import React, { useRef, useState, useEffect } from "react";
import { useFrame } from "react-three-fiber";
import { useGLTF, useAnimations } from "@react-three/drei";

const Skeleton = (props) => {
  const group = useRef();
  const { nodes, materials, animations } = useGLTF("../../../Xbot.glb");
  const { actions } = useAnimations(animations, group);
  const [active, setActive] = useState(false);

  return (
    <group ref={group} {...props} dispose={null} position={[0, -0.9, 0]}>
      <group scale={[0.01, 0.01, 0.01]}>
        <primitive object={nodes.mixamorigHips} />
        <skinnedMesh
          material={materials.Beta_Joints_MAT}
          geometry={nodes.Beta_Joints.geometry}
          skeleton={nodes.Beta_Joints.skeleton}
          onClick={(e) => setActive(!active)}
          color={active ? "hotpink" : "orange"}
        />
        <skinnedMesh
          material={materials["asdf1:Beta_HighLimbsGeoSG2"]}
          geometry={nodes.Beta_Surface.geometry}
          skeleton={nodes.Beta_Surface.skeleton}
          onClick={(e) => setActive(!active)}
          color={active ? "hotpink" : "orange"}
        />
      </group>
    </group>
  );
};

export default Skeleton;
