import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

import classes from "./ActivityParameterPage.module.scss";
import TopBar from "../../components/TopBar/TopBar";
import ActivityParameterInfo from "../../components/ActivityParameterInfo/ActivityParameterInfo";
import SelectedActivities from "../../components/SelectedActivities/SelectedActivities";

const ActivityParameterPage = (props) => {
  //console.log(props.activityParameters);
  let nextButton = null;
  let pathName = "/activity-parameters";

  if (props.checkPopUp && props.allActivitiesCompleted) {
    pathName = "/video-parameters";
  } else {
  }
  if (props.hasActivityParameters) {
    nextButton = (
      <button
        className={classes.next}
        onClick={props.routeToVideoParameterPage}
      >
        <Link
          className={classes.link_next}
          to={{
            pathname: { pathName },
          }}
        >
          Next
        </Link>
      </button>
    );
  }

  let popUp = null;

  const activityparamquery = (
    <ActivityParameterInfo
      currentActivity={props.currentActivity}
      activityParameters={props.activityParameters}
      onChange={props.updateParametersHandler}
      switched={props.switched}
    />
  );

  return (
    <>
      <TopBar />
      <div className={classes.activityParameters}>
        <div className={classes.overlay_left} />
        <div className={classes.overlay_right} />
        <div className={classes.overlay_top} />
        <div className={classes.overlay_bottom} />
        <div className={classes.overlay}>
          <div className={classes.popup}>
            <div className={classes.square} />
            <h2 className={classes.warning}>
              Would you like to apply these parameters to all activities?
            </h2>
            <h3 className={classes.warningMessage}>
              You have to enter activity parameters for all activities.
            </h3>
            <div className={classes.actions}>
              <button className={classes.applyAll}>Yes, apply to all!</button>
              <button className={classes.manually}>
                No, I will apply manually
              </button>
            </div>
          </div>
        </div>
        <div className={classes.content}>
          <SelectedActivities
            selected={props.activities}
            onClick={props.switchActivity}
            currentActivity={props.currentActivity}
          />
          {activityparamquery}
        </div>
        <div className={classes.routeBottons}>
          <button className={classes.back} onClick={props.routeBackToHomePage}>
            <Link
              className={classes.link_back}
              to={{
                pathname: "/",
              }}
            >
              Back
            </Link>
          </button>
          {nextButton}
        </div>
      </div>
    </>
  );
};
export default ActivityParameterPage;
